mapper.insert:
		INSERT INTO departments
        		(
            		deptId
            		, deptName
            		, mgr
            		, locId
        		)
    		VALUES
        		(
        			#{deptId}
        			, #{deptName}
        			, #{mgr}
        			, #{locId}
        		)

mapper.update:
UPDATE departments
SET deptName = #{dept.empName}
	, deptId = #{dept.deptId}
	, mgr = #{dept.mgr}
	, locId = #{dept.locId}
WHERE deptId = #{id}
